      const cellCodeDivs = window.document.querySelectorAll(".cell &gt; .sourceCode");
      const fromCls = show ? "hidden" : "unhidden";
      const toCls = show ? "unhidden" : "hidden";
      for (let i=0; i&lt;cellCodeDivs.length; i++) {
