&lt;span id="cb9-31"&gt;&lt;a href="#cb9-31" aria-hidden="true" tabindex="-1"&gt;&lt;/a&gt;        ems &lt;span class="op"&gt;=&lt;/span&gt; []&lt;/span&gt;
