&lt;span id="cb27-6"&gt;&lt;a href="#cb27-6" aria-hidden="true" tabindex="-1"&gt;&lt;/a&gt;probe_medium_ids.to_json(&lt;/span&gt;
&lt;span id="cb27-7"&gt;&lt;a href="#cb27-7" aria-hidden="true" tabindex="-1"&gt;&lt;/a&gt;    &lt;span class="st"&gt;&quot;validation_datasets/probe_medium_ids.jsonl&quot;&lt;/span&gt;, orient&lt;span class="op"&gt;=&lt;/span&gt;&lt;span class="st"&gt;&quot;records&quot;&lt;/span&gt;, lines&lt;span class="op"&gt;=&lt;/span&gt;&lt;span class="va"&gt;True&lt;/span&gt;&lt;/span&gt;
&lt;span id="cb27-8"&gt;&lt;a href="#cb27-8" aria-hidden="true" tabindex="-1"&gt;&lt;/a&gt;)&lt;/span&gt;
&lt;span id="cb27-9"&gt;&lt;a href="#cb27-9" aria-hidden="true" tabindex="-1"&gt;&lt;/a&gt;probe_hard_ids.to_json(&lt;/span&gt;
&lt;span id="cb27-10"&gt;&lt;a href="#cb27-10" aria-hidden="true" tabindex="-1"&gt;&lt;/a&gt;    &lt;span class="st"&gt;&quot;validation_datasets/probe_hard_ids.jsonl&quot;&lt;/span&gt;, orient&lt;span class="op"&gt;=&lt;/span&gt;&lt;span class="st"&gt;&quot;records&quot;&lt;/span&gt;, lines&lt;span class="op"&gt;=&lt;/span&gt;&lt;span class="va"&gt;True&lt;/span&gt;&lt;/span&gt;
&lt;span id="cb27-11"&gt;&lt;a href="#cb27-11" aria-hidden="true" tabindex="-1"&gt;&lt;/a&gt;)&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;
&lt;/details&gt;
&lt;/div&gt;
